mapper.select:
SELECT R.*
FROM
`rating` AS R
WHERE 1
AND R.relTypeCode = #{relTypeCode}
AND R.relId =
#{relId}
AND R.clientId = #{clientId}

mapper.delete:
DELETE FROM `rating`
WHERE 1
AND relTypeCode =
#{relTypeCode}
AND relId = #{relId}
AND clientId = #{clientId}

mapper.update:
UPDATE `rating`


		updateDate = NOW(),
		`point` = #{point}


WHERE 1
AND `relTypeCode` = #{relTypeCode}
AND `relId` = #{relId}
AND
`clientId` = #{clientId}
