mapper.select:
SELECT *
FROM `event`
WHERE 1
AND	relTypeCode = 'order'
AND	relTypeCode2 = #{relTypeCode2}
AND	relId = #{relId}
AND	relId2 = #{relId2}

mapper.update:
UPDATE `event`

	updateDate = NOW()

		, accept = #{accept}


		, stepLevel = #{stepLevel}


		, directOrder = #{directOrder}


		, region = #{region}


WHERE 1
AND	relTypeCode = 'order'
AND	relTypeCode2 = #{relTypeCode2}
AND	relId = #{relId}
AND	relId2 = #{relId2}

UPDATE `event`
updateDate = NOW() , accept = 0 , stepLevel = 0 , directOrder = 0 , alertCheckStatus = 1
WHERE 1 AND relTypeCode = 'order' AND relTypeCode2 = #{relTypeCode2} AND relId2 = #{relId2}
